package com.madgrid.dao;import java.util.List;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.Query;import org.apache.ojb.broker.query.QueryByCriteria;import com.madgrid.model.AdminUser;public class AdminUserDAO{				public void setAdminUser( AdminUser adminUser)  throws Exception{		PersistenceBroker broker = null;			try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( adminUser);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw ex;		}finally {			if (broker != null) {				broker.close();			}		}	}		public void deleteAdminUser( AdminUser adminUser)  throws Exception{		PersistenceBroker broker = null;				try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.delete( adminUser);			broker.commitTransaction();		}catch ( PersistenceBrokerException ex){			broker.abortTransaction();						throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<AdminUser> getAdminUserListByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {						Query query = new QueryByCriteria( AdminUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<AdminUser>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public int getAdminUserCountByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {			Query query = new QueryByCriteria( AdminUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return broker.getCount( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AdminUser getAdminUserById( Integer id) throws Exception	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				if( id.intValue() == 0)			return null;				try {			criteria.addEqualTo		( "id", id);						Query query = new QueryByCriteria( AdminUser.class, criteria);			broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AdminUser) broker.getObjectByQuery(query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AdminUser getAdminUserByCriteria( Criteria criteria) throws Exception	{		PersistenceBroker 	broker 		= null;		try {			Query query = new QueryByCriteria( AdminUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AdminUser)broker.getObjectByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}	}