package com.madgrid.dao;import java.util.List;import java.util.StringTokenizer;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.Query;import org.apache.ojb.broker.query.QueryByCriteria;import com.madgrid.admin.util.Utils;import com.madgrid.model.AffiliationContact;public class AffiliationContactDAO{				public void setAffiliationContact( AffiliationContact affiliationContact)  throws Exception{		PersistenceBroker broker = null;			try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( affiliationContact);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw ex;		}finally {			if (broker != null) {				broker.close();			}		}	}		public void deleteAffiliationContact( AffiliationContact affiliationContact)  throws Exception{		PersistenceBroker broker = null;				try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.delete( affiliationContact);			broker.commitTransaction();		}catch ( PersistenceBrokerException ex){			broker.abortTransaction();						throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<AffiliationContact> getAffiliationContactListByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {						Query query = new QueryByCriteria( AffiliationContact.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<AffiliationContact>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public int getAffiliationContactCountByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {			Query query = new QueryByCriteria( AffiliationContact.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return broker.getCount( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AffiliationContact getAffiliationContactById( Integer id) throws Exception	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				if( id.intValue() == 0)			return null;				try {			criteria.addEqualTo		( "id", id);						Query query = new QueryByCriteria( AffiliationContact.class, criteria);			broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AffiliationContact) broker.getObjectByQuery(query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AffiliationContact getAffiliationContactByCriteria( Criteria criteria) throws Exception	{		PersistenceBroker 	broker 		= null;		try {			Query query = new QueryByCriteria( AffiliationContact.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AffiliationContact)broker.getObjectByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<AffiliationContact> getAffiliationContactListByCriteriaAndRange(Criteria criteria, String order, int index, int offset) throws Exception	{		PersistenceBroker broker = null;		try {						QueryByCriteria query = new QueryByCriteria( AffiliationContact.class, criteria);			if( !Utils.nullOrBlank(order)){				StringTokenizer stringTokenizer = new StringTokenizer( order, ",");				while (stringTokenizer.hasMoreElements()){					String token = stringTokenizer.nextToken();					query.addOrderBy( token, token.startsWith("-")?true:false);				}			}						query.setStartAtIndex( index);			query.setEndAtIndex( index + offset);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<AffiliationContact>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}	}