package com.madgrid.dao;import java.util.List;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.Query;import org.apache.ojb.broker.query.QueryByCriteria;import com.madgrid.admin.util.Utils;import com.madgrid.model.AffiliationUser;import com.madgrid.model.User;public class AffiliationUserDAO{				public void setAffiliationUser( AffiliationUser affiliationUser)  throws Exception{		PersistenceBroker broker = null;			try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( affiliationUser);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw ex;		}finally {			if (broker != null) {				broker.close();			}		}	}		public void deleteAffiliationUser( AffiliationUser affiliationUser)  throws Exception{		PersistenceBroker broker = null;				try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.delete( affiliationUser);			broker.commitTransaction();		}catch ( PersistenceBrokerException ex){			broker.abortTransaction();						throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<AffiliationUser> getAffiliationUserListByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {						Query query = new QueryByCriteria( AffiliationUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<AffiliationUser>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<AffiliationUser> getAffiliationUserListByCriteria(Criteria criteria, String order) throws Exception	{		PersistenceBroker broker = null;		try {						QueryByCriteria query = new QueryByCriteria( AffiliationUser.class, criteria);						if( !Utils.nullOrBlank(order)){				query.addOrderBy( order, order.startsWith("-")?true:false);			}						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<AffiliationUser>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public int getAffiliationUserCountByCriteria(Criteria criteria) throws Exception	{		PersistenceBroker broker = null;		try {			Query query = new QueryByCriteria( AffiliationUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return broker.getCount( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AffiliationUser getAffiliationUserById( Integer id) throws Exception	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				if( id.intValue() == 0)			return null;				try {			criteria.addEqualTo		( "id", id);						Query query = new QueryByCriteria( AffiliationUser.class, criteria);			broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AffiliationUser) broker.getObjectByQuery(query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public AffiliationUser getAffiliationUserByCriteria( Criteria criteria) throws Exception	{		PersistenceBroker 	broker 		= null;		try {			Query query = new QueryByCriteria( AffiliationUser.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( AffiliationUser)broker.getObjectByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}	}