package com.madgrid.dao;import java.util.List;import java.util.StringTokenizer;import org.apache.ojb.broker.PersistenceBroker;import org.apache.ojb.broker.PersistenceBrokerException;import org.apache.ojb.broker.PersistenceBrokerFactory;import org.apache.ojb.broker.query.Criteria;import org.apache.ojb.broker.query.Query;import org.apache.ojb.broker.query.QueryByCriteria;import com.madgrid.admin.util.Utils;import com.madgrid.model.GridHistoric;public class GridHistoricDAO{				public void setGridHistoric( GridHistoric gridHistoric)  throws Exception{		PersistenceBroker broker = null;			try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.store( gridHistoric);			broker.commitTransaction();		}catch (PersistenceBrokerException ex){			broker.abortTransaction();			throw ex;		}finally {			if (broker != null) {				broker.close();			}		}	}		public void deleteGridHistoric( GridHistoric gridHistoric)  throws Exception{		PersistenceBroker broker = null;				try{			broker = PersistenceBrokerFactory.defaultPersistenceBroker();			broker.beginTransaction();			broker.delete( gridHistoric);			broker.commitTransaction();		}catch ( PersistenceBrokerException ex){			broker.abortTransaction();						throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<GridHistoric> getGridHistoricListByCriteria(Criteria criteria, String order) throws Exception	{		PersistenceBroker broker = null;		try {						QueryByCriteria query = new QueryByCriteria( GridHistoric.class, criteria);						if( !Utils.nullOrBlank(order)){				query.addOrderBy( order, order.startsWith("-")?true:false);			}						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<GridHistoric>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public List<GridHistoric> getGridHistoricListByCriteriaAndRange(Criteria criteria, String order, int index, int offset) throws Exception	{		PersistenceBroker broker = null;		try {						QueryByCriteria query = new QueryByCriteria( GridHistoric.class, criteria);			if( !Utils.nullOrBlank(order)){				StringTokenizer stringTokenizer = new StringTokenizer( order, ",");				while (stringTokenizer.hasMoreElements()){					String token = stringTokenizer.nextToken();					query.addOrderBy( token, token.startsWith("-")?true:false);				}			}						query.setStartAtIndex( index);			query.setEndAtIndex( index + offset);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return (List<GridHistoric>)broker.getCollectionByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public GridHistoric getGridHistoricById( Integer id) throws Exception	{		Criteria 			criteria	= new Criteria();		PersistenceBroker 	broker 		= null;				if( id.intValue() == 0)			return null;				try {			criteria.addEqualTo		( "id", id);						Query query = new QueryByCriteria( GridHistoric.class, criteria);			broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( GridHistoric) broker.getObjectByQuery(query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}		public GridHistoric getGridHistoricByCriteria( Criteria criteria) throws Exception	{		PersistenceBroker 	broker 		= null;		try {			Query query = new QueryByCriteria( GridHistoric.class, criteria);						broker = PersistenceBrokerFactory.defaultPersistenceBroker();				return ( GridHistoric)broker.getObjectByQuery( query);		}catch ( PersistenceBrokerException ex) {			throw ex;		}finally {			if ( broker != null) {				broker.close();			}		}	}	}